CREATE DATABASE  IF NOT EXISTS `curso`;
USE `curso`;
DROP TABLE IF EXISTS `menu`;
CREATE TABLE `menu` (`id` int(11) NOT NULL AUTO_INCREMENT,`pagina` varchar(45) NOT NULL,`link` varchar(45) NOT NULL,`principal` char(1) NOT NULL,`conteudo` text NOT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=latin1;
LOCK TABLES `menu` WRITE;
INSERT INTO `menu` VALUES (1,'home','Home','S','<h1>Site simples em PHP</h1>\n<p>Este site faz parte do primeiro Projeto a ser entregue, desenvolvido com o Twitter Bootstrap.</p>');
INSERT INTO `menu` VALUES (2,'empresa','Empresa','N','<h1>Empresa</h1>\n<p>A página Empresa faz parte do primeiro Projeto a ser entregue.</p>');
INSERT INTO `menu` VALUES (3,'produtos','Produtos','N','<h1>Produtos</h1>\n<p>Produzimos produtos de altíssima qualidade.</p>');
INSERT INTO `menu` VALUES (4,'servicos','Serviços','N','<h1>Serviços</h1>\n<p>Nossos serviços são referência em todo o país.</p>');
INSERT INTO `menu` VALUES (5,'contato','Contato','N','<h1>Contato</h1>\n<p>Preencha os dados do nosso Formulário de Contato.</p>\n\n<form class=\"form-horizontal\" action=\"contato-form\" method=\"post\">\n\n    <!-- nome -->\n    <div class=\"form-group\">\n        <label class=\"control-label col-xs-3\" for=\"inputNome\">Nome:</label>\n        <div class=\"col-xs-9\">\n            <input type=\"text\" class=\"form-control\" id=\"inputNome\" name=\"inputNome\" placeholder=\"Nome\">\n        </div>\n    </div>\n    <!-- email -->\n    <div class=\"form-group\">\n        <label class=\"control-label col-xs-3\" for=\"inputEmail\">Email:</label>\n        <div class=\"col-xs-9\">\n            <input type=\"email\" class=\"form-control\" id=\"inputEmail\" name=\"inputEmail\" placeholder=\"Email\">\n        </div>\n    </div>\n    <!-- Assunto -->\n    <div class=\"form-group\">\n        <label class=\"control-label col-xs-3\" for=\"inputAssunto\">Assunto:</label>\n        <div class=\"col-xs-9\">\n            <input type=\"text\" class=\"form-control\" id=\"inputAssunto\" name=\"inputAssunto\" placeholder=\"Assunto\">\n        </div>\n    </div>\n    <!-- Contato -->\n    <div class=\"form-group\">\n        <label class=\"control-label col-xs-3\" for=\"textMensagem\">Mensagem:</label>\n        <div class=\"col-xs-9\">\n            <textarea rows=\"3\" class=\"form-control\" id=\"textMensagem\" name=\"textMensagem\" placeholder=\"Mensagem\"></textarea>\n        </div>\n    </div>\n    <br>\n    <!-- Botões Enviar e Limpar -->\n    <div class=\"form-group\">\n        <div class=\"col-xs-offset-3 col-xs-9\">\n            <input type=\"submit\" class=\"btn btn-primary\" value=\"Enviar\">\n            <input type=\"reset\" class=\"btn btn-default\" value=\"Limpar\">\n        </div>\n    </div>\n\n</form>');
UNLOCK TABLES;
